INSERT INTO bancs (Text, CategoryId) SELECT 'O familie de crocodili trăia pe malul unui rîu din Angola şi avea doi pui. Crocodiloaica îi învăţa să înoate, iar crocodilul le aducea hrana. Într-o noapte, cei doi pui tresar din somn şi încep să strige:\n- Jos segregaţia rasială, trăiască libertatea negrilor, trăiască Patrice Lumumba!\nDupă ce i-au potolit şi i-au culcat din nou, crocodiloaica îl ceartă pe crocodil:\n- Ţi-am spus să nu le mai dai copiilor să mănînce partizani că vezi ce urmări sînt!', (SELECT Id FROM categories WHERE Name = 'Animale') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'O familie de crocodili trăia pe malul unui rîu din Angola şi avea doi pui. Crocodiloaica îi învăţa să înoate, iar crocodilul le aducea hrana. Într-o noapte, cei doi pui tresar din somn şi încep să strige:\n- Jos segregaţia rasială, trăiască libertatea negrilor, trăiască Patrice Lumumba!\nDupă ce i-au potolit şi i-au culcat din nou, crocodiloaica îl ceartă pe crocodil:\n- Ţi-am spus să nu le mai dai copiilor să mănînce partizani că vezi ce urmări sînt!');
INSERT INTO bancs (Text, CategoryId) SELECT 'Ursul merge prin pădure. Îl întîlneşte pe iepuraş şi-l întreabă: \n- Iepuraşule, nu vrei să vii cu mine să ne dăm cu motocicleta? \n- Ba da, răspunde iepuraşul şi se urcă pe motocicletă. Merg cu optzeci de kilometri la oră, cînd ursul îl întreabă: \n- Iepuraşule, nu ţi-e frică? \n- Nu. \nMerg cu o sută la oră. Ursul întreabă din nou: \n- Iepuraşule, nu ţi-e frică? \n- Nu. \nSe opresc, iepurasul merge acasă. A doua zi, vine cu motocicleta lui, o Honda. \n- Haide, ursule, acum te plimb eu cu motocicleta! \nMerg cu o sută cincizeci. Iepurasul întreabă: \n- Nu ţi-e frică? \n- Nu. \n- Mi-e mie, spune iepuraşul, nu ajung la frînă.', (SELECT Id FROM categories WHERE Name = 'Animale') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Ursul merge prin pădure. Îl întîlneşte pe iepuraş şi-l întreabă: \n- Iepuraşule, nu vrei să vii cu mine să ne dăm cu motocicleta? \n- Ba da, răspunde iepuraşul şi se urcă pe motocicletă. Merg cu optzeci de kilometri la oră, cînd ursul îl întreabă: \n- Iepuraşule, nu ţi-e frică? \n- Nu. \nMerg cu o sută la oră. Ursul întreabă din nou: \n- Iepuraşule, nu ţi-e frică? \n- Nu. \nSe opresc, iepurasul merge acasă. A doua zi, vine cu motocicleta lui, o Honda. \n- Haide, ursule, acum te plimb eu cu motocicleta! \nMerg cu o sută cincizeci. Iepurasul întreabă: \n- Nu ţi-e frică? \n- Nu. \n- Mi-e mie, spune iepuraşul, nu ajung la frînă.');
INSERT INTO bancs (Text, CategoryId) SELECT 'Într-un apartament al unui bloc select de locuinţe locuieşte o fată bătrînă. O vecină o întreaba mereu de ce nu se căsătoreşte, de ce nu-şi găseşte şi ea un bărbat cumsecade.\n- De ce? răspunde bătrîna domnişoară. Doar am un cîine, un papagal şi o pisică. Astea-mi sînt destule pînă peste cap.\n- Dar, totuşi, aceste animale nu pot ţine loc de soţ.\n- Cum să nu? Cîinele mîrîie mereu, papagalul injură toată ziua, iar pisica-i plecată toată noaptea.', (SELECT Id FROM categories WHERE Name = 'Animale') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Într-un apartament al unui bloc select de locuinţe locuieşte o fată bătrînă. O vecină o întreaba mereu de ce nu se căsătoreşte, de ce nu-şi găseşte şi ea un bărbat cumsecade.\n- De ce? răspunde bătrîna domnişoară. Doar am un cîine, un papagal şi o pisică. Astea-mi sînt destule pînă peste cap.\n- Dar, totuşi, aceste animale nu pot ţine loc de soţ.\n- Cum să nu? Cîinele mîrîie mereu, papagalul injură toată ziua, iar pisica-i plecată toată noaptea.');
