INSERT INTO bancs (Text, CategoryId) SELECT 'O haită de lupi vrea să treacă un pod, dar le sare în faţă iepuraşul strigînd:\n- Lupilor, sînt duri printre voi?\n- Nu!\n- Dacă vreţi să treceţi, trebuie să îmi daţi fiecare cîte 2.000 lei.\nA doua zi, se întîmplă acelaşi lucru.\nA treia zi, se gîndesc lupii şi îşi aleg trei duri, după care pornesc la drum. Iepuraşul le sare din nou în drum şi îi întreabă:\n- Lupilor, sînt duri printre voi?\n- Da! zic cei trei duri şi ies la iveală.\n- Atunci, ursule, ia de la ăştia trei cîte 5.000 lei şi de la restul iau eu 2.000 lei.', (SELECT Id FROM categories WHERE Name = 'Animale') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'O haită de lupi vrea să treacă un pod, dar le sare în faţă iepuraşul strigînd:\n- Lupilor, sînt duri printre voi?\n- Nu!\n- Dacă vreţi să treceţi, trebuie să îmi daţi fiecare cîte 2.000 lei.\nA doua zi, se întîmplă acelaşi lucru.\nA treia zi, se gîndesc lupii şi îşi aleg trei duri, după care pornesc la drum. Iepuraşul le sare din nou în drum şi îi întreabă:\n- Lupilor, sînt duri printre voi?\n- Da! zic cei trei duri şi ies la iveală.\n- Atunci, ursule, ia de la ăştia trei cîte 5.000 lei şi de la restul iau eu 2.000 lei.');
INSERT INTO bancs (Text, CategoryId) SELECT 'Trecînd pe uliţa unei comune, maşina calcă un cocoş. Şoferul coboară, ia pasărea, se uită împrejur şi vede o bătrînică în fundul curţii. O strigă şi întreabă:\n- Mătuşică, cocoşul ăsta era al dumitale?\nBătrîna se apropie, priveşte şi răspunde:\n- Nu, eu nu am avut niciodată păsări aşa turtite!', (SELECT Id FROM categories WHERE Name = 'Animale') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Trecînd pe uliţa unei comune, maşina calcă un cocoş. Şoferul coboară, ia pasărea, se uită împrejur şi vede o bătrînică în fundul curţii. O strigă şi întreabă:\n- Mătuşică, cocoşul ăsta era al dumitale?\nBătrîna se apropie, priveşte şi răspunde:\n- Nu, eu nu am avut niciodată păsări aşa turtite!');
