INSERT INTO bancs (Text, CategoryId) SELECT 'Iepuraşul se duce la vizuina Lupului, dă buzna înăuntru şi-l ia pe Lupişor la întrebări: "Băi urîtule! Ia cheamă-l pe tac-tu''!".\nApare Lupul: "Ce vrei, mă?".\nIepuraşul îi arde doi pumni (ZBANG! ZBANG!) şi fuge.\nLupul: "Mamă, îl nenorocesc pe ăsta, îl rup", etc.\nA doua zi la fel, a treia - acelaşi lucru, se enervează Lupul şi-l aşteaptă după uţă. Intră iepurele: "Unde eşti, băi lupule?".\nÎl prinde Lupul de gît: "Aha, nemernicule! Ia să vezi ce omor îţi dau eu acu''!".\n"Stai, Lupule, am dreptul la o ultimă dorinţă".\n"Bine, mă. Ce dorinţă ai?".\n"Păi vreau să fac o piruetă, mie mi-a plăcut mult coregrafia", etc.\nÎi da Lupul drumul, face iepuraşul o piruetă, îi dă doi pumni (ZBANG! ZBANG!), după care fuge.\nLupul, gînditor: "Băi, ce coregrafie tîmpită!"', (SELECT Id FROM categories WHERE Name = 'Animale') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Iepuraşul se duce la vizuina Lupului, dă buzna înăuntru şi-l ia pe Lupişor la întrebări: "Băi urîtule! Ia cheamă-l pe tac-tu''!".\nApare Lupul: "Ce vrei, mă?".\nIepuraşul îi arde doi pumni (ZBANG! ZBANG!) şi fuge.\nLupul: "Mamă, îl nenorocesc pe ăsta, îl rup", etc.\nA doua zi la fel, a treia - acelaşi lucru, se enervează Lupul şi-l aşteaptă după uţă. Intră iepurele: "Unde eşti, băi lupule?".\nÎl prinde Lupul de gît: "Aha, nemernicule! Ia să vezi ce omor îţi dau eu acu''!".\n"Stai, Lupule, am dreptul la o ultimă dorinţă".\n"Bine, mă. Ce dorinţă ai?".\n"Păi vreau să fac o piruetă, mie mi-a plăcut mult coregrafia", etc.\nÎi da Lupul drumul, face iepuraşul o piruetă, îi dă doi pumni (ZBANG! ZBANG!), după care fuge.\nLupul, gînditor: "Băi, ce coregrafie tîmpită!"');
INSERT INTO bancs (Text, CategoryId) SELECT 'Într-o zi se plimba iepuraşul prin pădure şi se întilneşte cu vulpea.\n- Bună, ce mai faci, iepuraşule?\n- O, bine, vulpeo, mă simt excelent, sînt într-o formă grozavă, dacă mă întilnesc cu ursul, îi dau cîteva labe de-l nenorocesc, praf îl fac.\n- Extraordinar.\nMai merge el ceva, se întilneşte cu lupul.\n- Cum o mai duci, iepuraşule?\n- Nemaipomenit, mă simt excelent, sînt într-o formă grozavă, dacă mă întîlnesc cu ursul, îi dau cîteva labe de-l nenorocesc, praf îl fac.\n- Uluitor.\nMai merge cît mai merge, iată că vine chiar ursul.\n- Hai salut, iepuraşule, am auzit că-ţi merge nemaipomenit, ce mai faci?\n- Ce să fac, nene ursule, ia, mă plimb şi eu prin pădure şi vorbesc prostii...', (SELECT Id FROM categories WHERE Name = 'Animale') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Într-o zi se plimba iepuraşul prin pădure şi se întilneşte cu vulpea.\n- Bună, ce mai faci, iepuraşule?\n- O, bine, vulpeo, mă simt excelent, sînt într-o formă grozavă, dacă mă întilnesc cu ursul, îi dau cîteva labe de-l nenorocesc, praf îl fac.\n- Extraordinar.\nMai merge el ceva, se întilneşte cu lupul.\n- Cum o mai duci, iepuraşule?\n- Nemaipomenit, mă simt excelent, sînt într-o formă grozavă, dacă mă întîlnesc cu ursul, îi dau cîteva labe de-l nenorocesc, praf îl fac.\n- Uluitor.\nMai merge cît mai merge, iată că vine chiar ursul.\n- Hai salut, iepuraşule, am auzit că-ţi merge nemaipomenit, ce mai faci?\n- Ce să fac, nene ursule, ia, mă plimb şi eu prin pădure şi vorbesc prostii...');
INSERT INTO bancs (Text, CategoryId) SELECT 'Vine iepuraşul la urs.\n- Nene ursule, hai la bar că fac eu cinste.\n- O, bun, se bucură ursul, hai.\nAjung ei la bar, vine barmanul. Zice iepuraşul:\n- Barman, o ţuică pentru mine şi o Cola pentru urs.\nSe supără ursul, îl scoate pe iepuraş afară şi spune:\n- Ascultă, iepuraşule, noi sîntem prieteni, da'' dacă-mi mai faci o fază din asta, să ma umileşti în public, te bat de te las lat.\nA doua zi, iar vine iepuraşul la urs.\n- Hai ursule la bar că fac cinste.\nSe duc, vine barmanul, zice iepuraşul:\n- O ţuică pentru mine şi doua Cola pentru urs.\nÎl scoate ursul pe iepuras afara şi îl bate de-l face praf.\nA treia zi:\n- Nene ursule, hai la bar că iar fac cinste.\nMerg ei iar, se aşează la bar, vine barmanul, iepuraşul:\n- Barman, o ţuică pentru mine şi o Cola pentru urs că daca bea două iar se-mbată şi nu mai ştie ce face.', (SELECT Id FROM categories WHERE Name = 'Animale') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Vine iepuraşul la urs.\n- Nene ursule, hai la bar că fac eu cinste.\n- O, bun, se bucură ursul, hai.\nAjung ei la bar, vine barmanul. Zice iepuraşul:\n- Barman, o ţuică pentru mine şi o Cola pentru urs.\nSe supără ursul, îl scoate pe iepuraş afară şi spune:\n- Ascultă, iepuraşule, noi sîntem prieteni, da'' dacă-mi mai faci o fază din asta, să ma umileşti în public, te bat de te las lat.\nA doua zi, iar vine iepuraşul la urs.\n- Hai ursule la bar că fac cinste.\nSe duc, vine barmanul, zice iepuraşul:\n- O ţuică pentru mine şi doua Cola pentru urs.\nÎl scoate ursul pe iepuras afara şi îl bate de-l face praf.\nA treia zi:\n- Nene ursule, hai la bar că iar fac cinste.\nMerg ei iar, se aşează la bar, vine barmanul, iepuraşul:\n- Barman, o ţuică pentru mine şi o Cola pentru urs că daca bea două iar se-mbată şi nu mai ştie ce face.');
