INSERT INTO bancs (Text, CategoryId) SELECT 'Vulpea, cea mai buna damă din pădure, se mărită cu lupul, cel mai bine tip (tot din pădure) şi după 9 luni naşte 12 purceluşi. Lupul o face pe vulpe curvă. Şi divorţează. \nVulpea, damă bună şi serioasă, se recăsătoreşte cu ursul. După 9 luni...12 purceluşi. Iar divorţ. \nVulpea, din nou damă bună, se recăsătoreşte cu leul. După încă 9 luni de zile... încă 12 purceluşi.\nMorala: toţi bărbaţii sînt porci.', (SELECT Id FROM categories WHERE Name = 'Animale') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Vulpea, cea mai buna damă din pădure, se mărită cu lupul, cel mai bine tip (tot din pădure) şi după 9 luni naşte 12 purceluşi. Lupul o face pe vulpe curvă. Şi divorţează. \nVulpea, damă bună şi serioasă, se recăsătoreşte cu ursul. După 9 luni...12 purceluşi. Iar divorţ. \nVulpea, din nou damă bună, se recăsătoreşte cu leul. După încă 9 luni de zile... încă 12 purceluşi.\nMorala: toţi bărbaţii sînt porci.');
INSERT INTO bancs (Text, CategoryId) SELECT 'În praful de la marginea străzii, o găina scormoneşte insistent după rîme. Trece pe acolo un tir şi-o calcă. Găina zace o vreme în praful de pe drum, după care îşi revine şi, scuturîndu-se, zice: \n- Ăsta da, cocoş…', (SELECT Id FROM categories WHERE Name = 'Animale') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'În praful de la marginea străzii, o găina scormoneşte insistent după rîme. Trece pe acolo un tir şi-o calcă. Găina zace o vreme în praful de pe drum, după care îşi revine şi, scuturîndu-se, zice: \n- Ăsta da, cocoş…');
