INSERT INTO bancs (Text, CategoryId) SELECT 'O blondă îl întâlneşte pe Ali în faţa unui magazin. Ali ţinea o maimuţă în braţe şi i-o dă blondei, rugând-o:\n- Asta e o Rhesus Balubadaris şi este foarte rară, ai grijă de ea… până ies din magazin, te rog!\nBlonda ia maimuţa în braţe şi aşteaptă. O altă blondă vine, se uită la maimuţă şi o întreabă pe blonda cu maimuţa:\n- Auzi, fată, ce e asta?\n- Un Rhesus Balubadaris.\n- Păi… şi de la cine îl ai?\n- De la Ali.\n- Păi, dragă, nu puteai şi tu să faci un chiuretaj?', (SELECT Id FROM categories WHERE Name = 'Bancuri noi') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'O blondă îl întâlneşte pe Ali în faţa unui magazin. Ali ţinea o maimuţă în braţe şi i-o dă blondei, rugând-o:\n- Asta e o Rhesus Balubadaris şi este foarte rară, ai grijă de ea… până ies din magazin, te rog!\nBlonda ia maimuţa în braţe şi aşteaptă. O altă blondă vine, se uită la maimuţă şi o întreabă pe blonda cu maimuţa:\n- Auzi, fată, ce e asta?\n- Un Rhesus Balubadaris.\n- Păi… şi de la cine îl ai?\n- De la Ali.\n- Păi, dragă, nu puteai şi tu să faci un chiuretaj?');
