INSERT INTO bancs (Text, CategoryId) SELECT 'O femeie la vreo 30 de ani, după ce şi-a rezolvat treburile în capitala de judeţ, voia să ajungă acum acasă şi nu mai avea bani. Se apropie de un taximetrist şi îi spune:\n- Uite, tre’ s-ajung şi eu în Trescovenii de Sus, dar nu am bani... Cum ajungem acolo ne socotim noi.\n- Bine, las’ că ne socotim noi pe drum!, zice taximetristul.\nDupă vreo oră, opreşte taximetristul pe un câmp, ia o pătură din portbagaj şi o aşterne pe lucernă. Femeia, îngrijorată, îi zice:\n- Vai, dar nu pot să fac asta, sunt măritată, am acasă doi copii!\n- Şi ce? Şi eu sunt însurat, am femeie, am copii, dar am şi 40 de iepuri de hrănit, aşa că taci şi rupe lucerna...', (SELECT Id FROM categories WHERE Name = 'Barbati si femei') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'O femeie la vreo 30 de ani, după ce şi-a rezolvat treburile în capitala de judeţ, voia să ajungă acum acasă şi nu mai avea bani. Se apropie de un taximetrist şi îi spune:\n- Uite, tre’ s-ajung şi eu în Trescovenii de Sus, dar nu am bani... Cum ajungem acolo ne socotim noi.\n- Bine, las’ că ne socotim noi pe drum!, zice taximetristul.\nDupă vreo oră, opreşte taximetristul pe un câmp, ia o pătură din portbagaj şi o aşterne pe lucernă. Femeia, îngrijorată, îi zice:\n- Vai, dar nu pot să fac asta, sunt măritată, am acasă doi copii!\n- Şi ce? Şi eu sunt însurat, am femeie, am copii, dar am şi 40 de iepuri de hrănit, aşa că taci şi rupe lucerna...');
