INSERT INTO bancs (Text, CategoryId) SELECT 'Se consideră o turmă de bizoni a cărei viteză este egală, evident, cu viteza celui mai lent bizon. Vînătorii, de orice natură, vînează cei mai lenţi bizoni, făcînd prin aceasta un bine turmei, a cărei viteză de daplasare va creşte. \nAnalog, creierul uman lucrează cu viteza celui mai lent neuron. Se ştie că alcoolul distruge neuronii, dar îi distruge pe cei mai lenţi, deci…', (SELECT Id FROM categories WHERE Name = 'Betivi') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Se consideră o turmă de bizoni a cărei viteză este egală, evident, cu viteza celui mai lent bizon. Vînătorii, de orice natură, vînează cei mai lenţi bizoni, făcînd prin aceasta un bine turmei, a cărei viteză de daplasare va creşte. \nAnalog, creierul uman lucrează cu viteza celui mai lent neuron. Se ştie că alcoolul distruge neuronii, dar îi distruge pe cei mai lenţi, deci…');
