INSERT INTO bancs (Text, CategoryId) SELECT 'O femeie se afla în sala de naşteri şi doctorul îi spune sa împingă. Apare capul copilului. Doctorul: "Dar copilul tău are ochi de chinez! Femeia: "Da, am auzit că sunt băieţi buni şi am zis să le dau o şansă!" Doctorul îi spune să împingă iar, de data asta ieşind corpul: "Doamne, dar copilul tău are corpul alb!"; "Da, am auzit că albii sunt băieţi buni şi am zis să le dau o şansă!" Doctorul îi spune să împingă si ultima dată: "Doamne, dar copilul tău are picioare negre!"; "Da, am auzit că negrii sunt băieţi buni şi am zis să le dau şi lor o şansă!" Doctorul scoate copilul, îi taie buricul şi îl loveşte peste fund să plângă. O întreabă pe femeie: "Cum o să te descurci cu un copil cu ochii oblici, cu corpul alb şi cu picioarele negre?" "Nu-i nimic, doctore, eu mă bucur că nu latră!"', (SELECT Id FROM categories WHERE Name = 'Doctori') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'O femeie se afla în sala de naşteri şi doctorul îi spune sa împingă. Apare capul copilului. Doctorul: "Dar copilul tău are ochi de chinez! Femeia: "Da, am auzit că sunt băieţi buni şi am zis să le dau o şansă!" Doctorul îi spune să împingă iar, de data asta ieşind corpul: "Doamne, dar copilul tău are corpul alb!"; "Da, am auzit că albii sunt băieţi buni şi am zis să le dau o şansă!" Doctorul îi spune să împingă si ultima dată: "Doamne, dar copilul tău are picioare negre!"; "Da, am auzit că negrii sunt băieţi buni şi am zis să le dau şi lor o şansă!" Doctorul scoate copilul, îi taie buricul şi îl loveşte peste fund să plângă. O întreabă pe femeie: "Cum o să te descurci cu un copil cu ochii oblici, cu corpul alb şi cu picioarele negre?" "Nu-i nimic, doctore, eu mă bucur că nu latră!"');
INSERT INTO bancs (Text, CategoryId) SELECT 'Un tip îşi sună disperat medicul de familie:\n- Doctore, vino repede, nevastă-mea are apendicită...\n- Nu se poate, anul trecut am operat-o de apendicită. Ai văzut tu pe cineva care să aiba două apendice?\n- Nu, tîmpitule! Dar tu n-ai mai văzut pe cineva să aibă două neveste?', (SELECT Id FROM categories WHERE Name = 'Doctori') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Un tip îşi sună disperat medicul de familie:\n- Doctore, vino repede, nevastă-mea are apendicită...\n- Nu se poate, anul trecut am operat-o de apendicită. Ai văzut tu pe cineva care să aiba două apendice?\n- Nu, tîmpitule! Dar tu n-ai mai văzut pe cineva să aibă două neveste?');
