INSERT INTO bancs (Text, CategoryId) SELECT 'Iţic îl întreabă pe rabin:\n- E adevărat că în anul 2000 va fi un război mare şi sfîrşitul lumii?\n- Nuuuu-i adevărat! În anul 2000 va fi o continuă luptă pentru pace! Va exista o singură ţară, URSS. Capitala va fi la Washington şi va fi condusă de doi chinezi: Iţac si Şloim!"', (SELECT Id FROM categories WHERE Name = 'Evrei') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Iţic îl întreabă pe rabin:\n- E adevărat că în anul 2000 va fi un război mare şi sfîrşitul lumii?\n- Nuuuu-i adevărat! În anul 2000 va fi o continuă luptă pentru pace! Va exista o singură ţară, URSS. Capitala va fi la Washington şi va fi condusă de doi chinezi: Iţac si Şloim!"');
