INSERT INTO bancs (Text, CategoryId) SELECT 'FBI, CIA, LAPD (poliţia din Los Angeles) se lăudau fiecare că ei sînt cei mai buni poliţişti. Aşa că preşedintele le-a dat un test. A luat un iepure şi l-a băgat într-o pădure. Ei trebuiau să-l găsească.\nIntră CIA. 2 luni, informatori, urmăriri, înregistrări... nimic!\nConcluzia: iepurele nu există.\nIntră FBI. 3 săptămîni, poliţişti sub acoperire, iar urmăriri, nimic!\nLa sfîrşit intră în pădure, omoară toate animalele şi dau foc. Oricum, asta merita iepurele ăla.\nÎn sfîrşit, intră cei de la LAPD. După 2 ore, ies cu un urs bătut măr, care urla: "OK, OK, sînt un iepure!"', (SELECT Id FROM categories WHERE Name = 'Politisti') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'FBI, CIA, LAPD (poliţia din Los Angeles) se lăudau fiecare că ei sînt cei mai buni poliţişti. Aşa că preşedintele le-a dat un test. A luat un iepure şi l-a băgat într-o pădure. Ei trebuiau să-l găsească.\nIntră CIA. 2 luni, informatori, urmăriri, înregistrări... nimic!\nConcluzia: iepurele nu există.\nIntră FBI. 3 săptămîni, poliţişti sub acoperire, iar urmăriri, nimic!\nLa sfîrşit intră în pădure, omoară toate animalele şi dau foc. Oricum, asta merita iepurele ăla.\nÎn sfîrşit, intră cei de la LAPD. După 2 ore, ies cu un urs bătut măr, care urla: "OK, OK, sînt un iepure!"');
