INSERT INTO bancs (Text, CategoryId) SELECT 'Un poliţist discuta cu doi colegi:\n- Băi, copilul meu e prost, nu ştie cît face 1-1. \n- Bă, al meu e şi mai prost, zice al doilea, nu ştie cît face 1+1.\nAl treilea:\n- Veniţi la mine acasă să vedeţi ce prost e al meu.\nAcasă la al treilea poliţist:\n- Bă, Costeluş, vino la tata, du-te la colţul blocului şi vezi dacă vin acasă.\n- Bine, tati, mă duc.\nDupă cinci minute:\n- Nu vii.\n- Vedeţi, bă?\nCei doi poliţişti:\n- Ce prost, parcă nu putea să se uite de pe balcon!', (SELECT Id FROM categories WHERE Name = 'Politisti') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Un poliţist discuta cu doi colegi:\n- Băi, copilul meu e prost, nu ştie cît face 1-1. \n- Bă, al meu e şi mai prost, zice al doilea, nu ştie cît face 1+1.\nAl treilea:\n- Veniţi la mine acasă să vedeţi ce prost e al meu.\nAcasă la al treilea poliţist:\n- Bă, Costeluş, vino la tata, du-te la colţul blocului şi vezi dacă vin acasă.\n- Bine, tati, mă duc.\nDupă cinci minute:\n- Nu vii.\n- Vedeţi, bă?\nCei doi poliţişti:\n- Ce prost, parcă nu putea să se uite de pe balcon!');
