INSERT INTO bancs (Text, CategoryId) SELECT 'Se plimba Adam prin grădina Raiului şi-l vede Dumnezeu cam trist.\n- Ce să-i fac eu băiatului ăstuia? Ah, gata, ştiu! Adame! Ia vino încoace!\nAdam vine.\n- Adam, ce zici, mă, îţi fac o femeie?\n- Ce-i aia, Doamne?\n- Ah! E-o chestie mişto de tot! Frumoasă, deşteaptă, înţelegătoare, iubitoare, temperamentală... ce mai!\n- Bine, Doamne, da'' de la mine ce vrei?\n- Păi am nevoie de atît: un ochi, un rinichi, un plămîn, un braţ, un picior, jumătate de colon, jumătate din emisfera dreaptă cerebrală, 3 litri de sînge şi cîţiva dinţi. Şi ţi-o fac, ştii cum? Frumoasă, deşteaptă, înţelegătoare, iubitoare...\nSe gîndeşte Adam şi zice:\n- Auzi, Doamne? Da'' dacă-ţi dau doar o coastă nu-ţi ajunge?', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Se plimba Adam prin grădina Raiului şi-l vede Dumnezeu cam trist.\n- Ce să-i fac eu băiatului ăstuia? Ah, gata, ştiu! Adame! Ia vino încoace!\nAdam vine.\n- Adam, ce zici, mă, îţi fac o femeie?\n- Ce-i aia, Doamne?\n- Ah! E-o chestie mişto de tot! Frumoasă, deşteaptă, înţelegătoare, iubitoare, temperamentală... ce mai!\n- Bine, Doamne, da'' de la mine ce vrei?\n- Păi am nevoie de atît: un ochi, un rinichi, un plămîn, un braţ, un picior, jumătate de colon, jumătate din emisfera dreaptă cerebrală, 3 litri de sînge şi cîţiva dinţi. Şi ţi-o fac, ştii cum? Frumoasă, deşteaptă, înţelegătoare, iubitoare...\nSe gîndeşte Adam şi zice:\n- Auzi, Doamne? Da'' dacă-ţi dau doar o coastă nu-ţi ajunge?');
INSERT INTO bancs (Text, CategoryId) SELECT 'Un băieţel privea atent la un preot care-şi repara gardul de la grădină.\n- De ce mă priveşti aşa de atent, fiule?\n- Vreau să aflu ce zice un preot cînd îşi dă cu ciocanul peste degete!', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Un băieţel privea atent la un preot care-şi repara gardul de la grădină.\n- De ce mă priveşti aşa de atent, fiule?\n- Vreau să aflu ce zice un preot cînd îşi dă cu ciocanul peste degete!');
INSERT INTO bancs (Text, CategoryId) SELECT 'Într-o seară, un popă se întorcea de la o împărtăşanie, pe o stradă cu "fetiţe". Una dintre ele îl abordează direct. Popa îşi făcu semnul crucii şi îi spuse:\n- O să-mi petrec nopţile rugîndu-mă pentru tine!\nProstituata îi răspunse:\n- Nu-i nevoie, părinte, cu 100.000 poţi să mă ai cînd vrei!', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Într-o seară, un popă se întorcea de la o împărtăşanie, pe o stradă cu "fetiţe". Una dintre ele îl abordează direct. Popa îşi făcu semnul crucii şi îi spuse:\n- O să-mi petrec nopţile rugîndu-mă pentru tine!\nProstituata îi răspunse:\n- Nu-i nevoie, părinte, cu 100.000 poţi să mă ai cînd vrei!');
INSERT INTO bancs (Text, CategoryId) SELECT 'Un preot catolic, unul baptist şi un rabin se gîndeau cum să împartă banii între Dumnezeu (fondul bisericii) şi ei.\nCatolicul: Eu o să trag o linie, arunc banii în sus şi ce pică în dreapta e al meu, ce pică în stînga e al Lui.\nBaptistul: Eu o să fac un cerc şi ce pică în el e al Lui, iar restul al meu.\nRabinul: Eu o să-i arunc în sus şi El o să-şi păstreze cît are nevoie.', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Un preot catolic, unul baptist şi un rabin se gîndeau cum să împartă banii între Dumnezeu (fondul bisericii) şi ei.\nCatolicul: Eu o să trag o linie, arunc banii în sus şi ce pică în dreapta e al meu, ce pică în stînga e al Lui.\nBaptistul: Eu o să fac un cerc şi ce pică în el e al Lui, iar restul al meu.\nRabinul: Eu o să-i arunc în sus şi El o să-şi păstreze cît are nevoie.');
