INSERT INTO bancs (Text, CategoryId) SELECT 'Un preot catolic, unul protestant şi un rabin, după o obositoare conferinţă asupra religiilor, s-au hotărît să petreacă seara pe marginea unui lac. Zis şi făcut. Merg ei la locul cu pricina, discută vrute şi nevrute, după care hotărăsc să facă o baie în lac. Se dezbracă ei, lasă hainele pe margine, şi - goi puşcă - se aruncă în lac. După ce ies, nici nu apucă să se îmbrace şi trei femei trec prin zonă, venind spre ei. Din reflex, preotul catolic şi cel protestant îşi acoperă zonele intime, însă rabinul îşi acoperă faţa. Contrariat, preotul catolic întreabă:\n- Bine, măi rabine, de ce nu faci şi tu ca noi, să îţi acoperi cu mîinile părtile intime?\nLa care rabinul răspunde:\n- Nu ştiu cum este în comunitatea voastră, însă în a mea eu sunt recunoscut după faţă.', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Un preot catolic, unul protestant şi un rabin, după o obositoare conferinţă asupra religiilor, s-au hotărît să petreacă seara pe marginea unui lac. Zis şi făcut. Merg ei la locul cu pricina, discută vrute şi nevrute, după care hotărăsc să facă o baie în lac. Se dezbracă ei, lasă hainele pe margine, şi - goi puşcă - se aruncă în lac. După ce ies, nici nu apucă să se îmbrace şi trei femei trec prin zonă, venind spre ei. Din reflex, preotul catolic şi cel protestant îşi acoperă zonele intime, însă rabinul îşi acoperă faţa. Contrariat, preotul catolic întreabă:\n- Bine, măi rabine, de ce nu faci şi tu ca noi, să îţi acoperi cu mîinile părtile intime?\nLa care rabinul răspunde:\n- Nu ştiu cum este în comunitatea voastră, însă în a mea eu sunt recunoscut după faţă.');
INSERT INTO bancs (Text, CategoryId) SELECT 'În timpul slujbei, vine un frate în fugă şi anunţă în gura mare: \n- Am două veşti, una bună şi alta proastă: azi am vorbit cu Dumnezeu la telefon. Deci Dumnezeu există! Aia proastă... a sunat de la Mecca.', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'În timpul slujbei, vine un frate în fugă şi anunţă în gura mare: \n- Am două veşti, una bună şi alta proastă: azi am vorbit cu Dumnezeu la telefon. Deci Dumnezeu există! Aia proastă... a sunat de la Mecca.');
INSERT INTO bancs (Text, CategoryId) SELECT 'Un preot tînăr era la prima lui slujbă. Emoţii mari, frisoane, etc. Mai marele lui, care trebuia să-l observe, îi zice să ia o gură de vodcă. Ăsta bea sănătos un pahar şi pleacă la slujbă.\nSlujba are un succes nebun în rîndul enoriaşilor, numai mai marele lui notează ceva în carneţel, nemulţumit. Îl cheamă cînd termină şi-i zice: \n- Să ţii minte: sînt cele zece porunci, nu "top 10"; este vorba despre "Tatăl, Fiul şi Sfîntul Duh", nu de "Moşul, fi-su şi fantoma"…', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Un preot tînăr era la prima lui slujbă. Emoţii mari, frisoane, etc. Mai marele lui, care trebuia să-l observe, îi zice să ia o gură de vodcă. Ăsta bea sănătos un pahar şi pleacă la slujbă.\nSlujba are un succes nebun în rîndul enoriaşilor, numai mai marele lui notează ceva în carneţel, nemulţumit. Îl cheamă cînd termină şi-i zice: \n- Să ţii minte: sînt cele zece porunci, nu "top 10"; este vorba despre "Tatăl, Fiul şi Sfîntul Duh", nu de "Moşul, fi-su şi fantoma"…');
INSERT INTO bancs (Text, CategoryId) SELECT 'Un preot trecea printr-un cartier ţigănesc. Un ţigănuş îl acostează:\n- Nene, nene!\n- Fiule, mie să-mi spui părinte, nu nene!\n- Mamă, mamă, începu să răcnească puradeul, vino repede că s-a întors tata!', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Un preot trecea printr-un cartier ţigănesc. Un ţigănuş îl acostează:\n- Nene, nene!\n- Fiule, mie să-mi spui părinte, nu nene!\n- Mamă, mamă, începu să răcnească puradeul, vino repede că s-a întors tata!');
INSERT INTO bancs (Text, CategoryId) SELECT 'Un pustnic creştin, îmbrăcat în zdrenţe, cu picioarele însîngerate de pietre şi spini, cu mintea încinsă de soare, alerga fără oprire prin nisipul deşertului şi striga către toate zările:\n- Am un răspuns! Am un răspuns! Cine are o întrebare?', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Un pustnic creştin, îmbrăcat în zdrenţe, cu picioarele însîngerate de pietre şi spini, cu mintea încinsă de soare, alerga fără oprire prin nisipul deşertului şi striga către toate zările:\n- Am un răspuns! Am un răspuns! Cine are o întrebare?');
INSERT INTO bancs (Text, CategoryId) SELECT 'Arhanghelul Gavril zboară în preajma lui Dumnezeu şi-i spune, arătînd de sus spre Mexicul proaspăt născut:\n- Dar, Doamne, la ce te gîndeşti? Ai facut o ţară de-a dreptul minunată! Ai dăruit-o cu două oceane, cu munţi înalţi, cu păduri dese şi rîuri adînci! Nu cumva ai pus prea multe într-o singură ţară?\n- Stai fără grijă, îi răspunde Dumnezeu. Am să pun şi mexicani.', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Arhanghelul Gavril zboară în preajma lui Dumnezeu şi-i spune, arătînd de sus spre Mexicul proaspăt născut:\n- Dar, Doamne, la ce te gîndeşti? Ai facut o ţară de-a dreptul minunată! Ai dăruit-o cu două oceane, cu munţi înalţi, cu păduri dese şi rîuri adînci! Nu cumva ai pus prea multe într-o singură ţară?\n- Stai fără grijă, îi răspunde Dumnezeu. Am să pun şi mexicani.');
INSERT INTO bancs (Text, CategoryId) SELECT 'Maica Tereza moare şi ajunge în Rai. Datorită luptei ei pentru cei săraci, se aşteaptă la o primire deosebită. Totuşi la uşa Raiului nimeni. Maica Tereza se uită printre gratii şi vede numai blitzuri de la aparate de fotografiat. După o oră de aşteptare, vine şi Sf. Petru care e întrebat ce s-a întîmplat de a durat atît. Petru se scuză şi zice:\n- Lady Di a sosit la noi!\n- Asta mă bucură, dar pentru asta a trebuit tot circul ăsta şi aţi lăsat şi toţi paparazzi ăştia înăuntru!\n- Ştiţi, spuse Petru, sfinţi ca dv. vin la noi în fiecare zi, dar din familia regală doar odată la 100 de ani se rătăceşte cîte unul pe aici…', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Maica Tereza moare şi ajunge în Rai. Datorită luptei ei pentru cei săraci, se aşteaptă la o primire deosebită. Totuşi la uşa Raiului nimeni. Maica Tereza se uită printre gratii şi vede numai blitzuri de la aparate de fotografiat. După o oră de aşteptare, vine şi Sf. Petru care e întrebat ce s-a întîmplat de a durat atît. Petru se scuză şi zice:\n- Lady Di a sosit la noi!\n- Asta mă bucură, dar pentru asta a trebuit tot circul ăsta şi aţi lăsat şi toţi paparazzi ăştia înăuntru!\n- Ştiţi, spuse Petru, sfinţi ca dv. vin la noi în fiecare zi, dar din familia regală doar odată la 100 de ani se rătăceşte cîte unul pe aici…');
