INSERT INTO bancs (Text, CategoryId) SELECT '- Părinte, recunosc, am încălcat sfaturile părinteşti. Tata mi-a interzis să mă duc la acea casă de desfrâu, dar eu nu am putut rezista şi i-am trecut pragul.\n- Urât, fiule, urât. Şi, desigur, ai văzut acolo ce nu trebuia să vezi...\n- Da, părinte. În primul rând, l-am văzut pe tata!', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = '- Părinte, recunosc, am încălcat sfaturile părinteşti. Tata mi-a interzis să mă duc la acea casă de desfrâu, dar eu nu am putut rezista şi i-am trecut pragul.\n- Urât, fiule, urât. Şi, desigur, ai văzut acolo ce nu trebuia să vezi...\n- Da, părinte. În primul rând, l-am văzut pe tata!');
INSERT INTO bancs (Text, CategoryId) SELECT 'Doi prieteni mor într-un accident de maşină. Unul merge în rai, iar celălalt în iad. Într-o zi, cel din rai se uită în jos şi-l vede pe cel din iad stînd cu o cutie de bere în mînă şi cu o blondă pe genunchi. Supărat, se duce la Dumnezeu: "Ce-i asta?! Cred că vreau să merg în iad; uite-te la prietenul meu de acolo!" Dumnezeu îi răspunde: "Uite-te mai bine: Cutia are o gaură în fund şi blonda, nu!"', (SELECT Id FROM categories WHERE Name = 'Religie') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'Doi prieteni mor într-un accident de maşină. Unul merge în rai, iar celălalt în iad. Într-o zi, cel din rai se uită în jos şi-l vede pe cel din iad stînd cu o cutie de bere în mînă şi cu o blondă pe genunchi. Supărat, se duce la Dumnezeu: "Ce-i asta?! Cred că vreau să merg în iad; uite-te la prietenul meu de acolo!" Dumnezeu îi răspunde: "Uite-te mai bine: Cutia are o gaură în fund şi blonda, nu!"');
