INSERT INTO bancs (Text, CategoryId) SELECT 'O femeie foarte seducătoare intră într-un bar, se duce la toaletă, iar cînd vine, se duce direct la barman şi începe să-l mîngăie tandru pe barba stufoasă: "Tu eşti şeful?", întreabă ea, mîngîindu-i faţa cu ambele mîini. "Nu, nu sunt eu", îi raspunde el. "Poţi să-l chemi pentru mine; vreau neapărat să vorbesc cu el?", spuse ea plimbîndu-şi mîinile prin faţa şi părul lui. "Mă tem că nu", răspunse barmanul vizibil excitat, "Pot să te ajut eu?". "Da", spuse ea, băgîndu-i două degete în gură şi lăsându-l să i le sugă, "Spune-i că nu e nici hîrtie igienică şi nici săpun la toaleta femeilor"', (SELECT Id FROM categories WHERE Name = 'Scirboase') WHERE NOT EXISTS (SELECT 1 FROM bancs WHERE Text = 'O femeie foarte seducătoare intră într-un bar, se duce la toaletă, iar cînd vine, se duce direct la barman şi începe să-l mîngăie tandru pe barba stufoasă: "Tu eşti şeful?", întreabă ea, mîngîindu-i faţa cu ambele mîini. "Nu, nu sunt eu", îi raspunde el. "Poţi să-l chemi pentru mine; vreau neapărat să vorbesc cu el?", spuse ea plimbîndu-şi mîinile prin faţa şi părul lui. "Mă tem că nu", răspunse barmanul vizibil excitat, "Pot să te ajut eu?". "Da", spuse ea, băgîndu-i două degete în gură şi lăsându-l să i le sugă, "Spune-i că nu e nici hîrtie igienică şi nici săpun la toaleta femeilor"');
